     </div>                            <div class="form-group">                                <label class="col-sm-2 control-label"><span style="color:red">*</span>重量kg</label>                                <div class="col-sm-10">                                    <input class="form-control pricecount" name="weight" placeholder="" value="{$data.weight}" >                                </div>                            </div>                            <div class="form-group">                                <label class="col-sm-2 control-label"><span style="color:red">*</span>服务收费</label>                                <div class="col-sm-10">                                    <span id="fwf" style="line-height: 34px"></span>                                    <input class="form-control" name="fee" placeholder="" value="{$data.fee}" >                                </div>                            </div>                            <input type="hidden" name="size" value="{$data.size}">                            <div class="form-group">                                <label class="col-sm-2 control-label"><span style="color:red">*</span>到达门店</label>                                <div class="col-sm-10">                                    <select class="form-control m-b" name="shopid" value="{$data.shopid}">                                        <?php                                        $SModel = new \Common\Model\DaishouShopModel();                                        $data1 = $SModel->order('id desc')->select();                                        foreach( $data1 as $key => $val) {                                            if($val["id"] == $data["shopid"]) {                                                echo "  <option value=\"".$val["id"]."\" >".$val["name"]."</option>";                                            }                                        }                                        foreach( $data1 as $key => $val) {                                            echo "  <option value=\"".$val["id"]."\" >".$val["name"]."</option>";                                        }                                        ?>                                    </select>                                </div>                            </div>                            <div class="form-group">                                <label class="col-sm-2 control-label"><span style="color:red">*</span>货架号</label>                                <div class="col-sm-10">                                    <input class="form-control" name="shelf" placeholder="" value="{$data.shelf}" >                                </div>                            </div>                            <div class="form-group">                                <label class="col-sm-2 control-label">门店备注</label>                                <div class="col-sm-10">                                    <input class="form-control" name="shopremark" placeholder="" value="{$data.shopremark}">                                </div>                            </div>                            <div class="form-group">                                <label class="col-sm-2 control-label"><span style="color:red">*</span>客户UID</label>                                <div class="col-sm-10">                                    <input name="uid" class="form-control" value="{$data.uid}">                                </div>                            </div>                            <div class="form-group">                                <div class="col-sm-4 col-sm-offset-2">                                    <input name="id" type="hidden" value="{$data.id}">                                    <button class="btn btn-primary" type="submit"><i class="fa fa-check"></i>&nbsp;添加</button>                                </div>                            </div>                